            delay: (el, i) => 150 * (i+1)
        })

        spell.addEventListener('click',function(){
            // console.log("My button works");
            more.classList.toggle('hide');
            document.querySelector('h1').style.cssText = `
            margin-top: 3rem;
                `;
        })
        </script>
    </body>
</html>